using AdventOfCode.Days;

namespace AdventOfCode.Tests
{
    public class Day6Test
    {
        [Fact]
        public void Test()
        {
            Assert.Equal(7, new Day6().Solve(@"mjqjpqmgbljsphdztnvjfqwrcgsmlb"));
            Assert.Equal(5, new Day6().Solve(@"bvwbjplbgvbhsrlpgdmjqwftvncz"));
            Assert.Equal(6, new Day6().Solve(@"nppdvjthqldpwncqszvftbrmjlhg"));
            Assert.Equal(10, new Day6().Solve(@"nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"));
            Assert.Equal(11, new Day6().Solve(@"zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"));
        }

        [Fact]
        public void TestReal()
        {
            string inputReal = @"";
            Assert.Equal(1598, new Day6().Solve(inputReal, 4));
            Assert.Equal(2414, new Day6().Solve(inputReal, 14));
        }
    }
}